                        plyr::summarise, 
                        N = sum(!is.na(Curiousity)),
                        Mean = mean(Curiousity, na.rm=TRUE),
                        SD = sd(Curiousity, na.rm=TRUE),
                        SE = SD/sqrt(N)
                        )
