new_data$Condition <- plyr::revalue(new_data$Condition, c("Ordinary"="Ordinary Experiences", "Extraordinary"="Extraordinary Experiences"))

graph <- ggplot2::ggplot(data=new_data, aes(x=time, y=Mean, group=Condition)) +
